DROP TABLE IF EXISTS `sight`;
CREATE TABLE `sight`  (
  `sight_id` int(10) UNSIGNED ZEROFILL NOT NULL AUTO_INCREMENT COMMENT '景点唯一标识(递增)',
  `sight_name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '景点名',
  `sight_type` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '景点类型',
  `state` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '景点所在洲',
  `country` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NOT NULL COMMENT '景点所在国家',
  `city` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL DEFAULT NULL COMMENT '景点所在城市',
  `score` decimal(2, 1) NULL DEFAULT NULL COMMENT '用户对景点的评分',
  `views` int(0) NULL DEFAULT NULL COMMENT '景点访问量',
  `description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci NULL COMMENT '景点的详细描述',
  `happiness_index` decimal(6, 2) NULL DEFAULT NULL COMMENT '幸福指数',
  `angry` decimal(6, 2) NULL DEFAULT NULL COMMENT '生气',
  `depress` decimal(6, 2) NULL DEFAULT NULL COMMENT '沮丧',
  `fear` decimal(6, 2) NULL DEFAULT NULL COMMENT '恐惧',
  `happy` decimal(6, 2) NULL DEFAULT NULL COMMENT '开心',
  `sad` decimal(6, 2) NULL DEFAULT NULL COMMENT '伤心',
  `surprise` decimal(6, 2) NULL DEFAULT NULL COMMENT '惊讶',
  `neutral` decimal(6, 2) NULL DEFAULT NULL COMMENT '无表情',
  PRIMARY KEY (`sight_id`) USING BTREE,
  FULLTEXT INDEX `fulltext_sight`(`sight_name`, `sight_type`, `state`, `country`, `city`, `description`)
) ENGINE = InnoDB AUTO_INCREMENT = 51 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_0900_ai_ci ROW_FORMAT = Dynamic;

